import React from 'react';
import { storiesOf } from '@storybook/react';
import { JSDokumentasjon } from './../../dokumentasjon';
import readme from './README.md';
import pkg from './package.json';
import Lenkeinnholdpanel from './src/index';

function customLinkCreator({ children, ...props }) { // eslint-disable-line react/prop-types
    return (
        <a { ...props } style={ { backgroundColor: 'cyan' } }>
            { children }
        </a>
    );
}

storiesOf('Paneler', module)
    .addWithSections('Lenkeinnholdpanel', () => (
        <section style={ { backgroundColor: '#efefef', padding: '1rem' } }>
            <Lenkeinnholdpanel href="#">
                <h1 className="typo-undertittel">Lenkeinnholdpanel med overskrift...</h1>
                <p>...og flere elementer</p>
            </Lenkeinnholdpanel>
            <Lenkeinnholdpanel href="#" linkCreator={ customLinkCreator }>Lenkeinnholdpanel</Lenkeinnholdpanel>
        </section>
    ), JSDokumentasjon(pkg, readme, Lenkeinnholdpanel));
