import PT from 'prop-types';
import React, { Component } from 'react';
import classnames from 'classnames';
import 'nav-frontend-lenkepanel-style'; // eslint-disable-line import/extensions

const cls = (className) => classnames('lenkepanel', className);

/**
 * Lenkepanel
 */
class Lenkepanel extends Component {
    render() {
        const { linkCreator, ...renderProps } = this.props;

        return linkCreator({
            ...renderProps,
            className: cls(renderProps.className),
            children: renderProps.children
        });
    }
}

Lenkepanel.propTypes = {
    /**
     * Klassenavn
     */
    className: PT.string,
    /**
     * Lenke (denne sendes som prop til linkCreator)
     */
    href: PT.string.isRequired,
    /**
     * Tekstinnhold for lenkepanel
     */
    children: PT.node.isRequired,
    /**
     * Funksjon som brukes for Ã¥ lage lenken. her kan du spesifisere din egen hvis du f.eks. bruker React Router
     */
    linkCreator: PT.func
};

Lenkepanel.defaultProps = {
    className: undefined,
    linkCreator: (props) => <a { ...props }>{ props.children }</a>
};

export default Lenkepanel;
