import React from 'react';
import { storiesOf } from '@storybook/react';
import { JSDokumentasjon } from './../../dokumentasjon';
import readme from './README.md';
import pkg from './package.json';
import LenkepanelInnhold from './src/index';

function customLinkCreator({ children, ...props }) { // eslint-disable-line react/prop-types
    return (
        <a {...props} style={{ backgroundColor: 'cyan' }}>
            { children }
        </a>
    );
}

storiesOf('Paneler', module)
    .addWithSections('LenkepanelInnhold', () => (
        <section style={{ backgroundColor: '#efefef', padding: '1rem' }}>
            <LenkepanelInnhold href="#">
                <h1 className="typo-undertittel">Lenkepanel med innhold, overskrift...</h1>
                <p>...og flere elementer</p>
            </LenkepanelInnhold>
            <LenkepanelInnhold href="#" linkCreator={customLinkCreator}>LenkepanelInnhold</LenkepanelInnhold>
        </section>
    ), JSDokumentasjon(pkg, readme, LenkepanelInnhold));
