import React from 'react';
import { Autocomplete, Checkbox } from './../';
class AutocompleteExample extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            value: '',
            showSpinner: false
        }
    }

    onChange(value) {
        this.setState({value});
    }

    render() {
        return (
            <span>

                <Autocomplete
                    placeholder="Velg en frukt"
                    items={[
                        {id: 'epler', label: 'Epler'},
                        {id: 'bananer', label: 'Bananer'},
                        {id: 'bringebaer', label: 'Bringebær'},
                        {id: 'pearer', label: 'Pærer'}
                    ]}
                    value={this.state.value}
                    shouldItemRender={(item, value) => item.label.toLowerCase().indexOf(value.toLowerCase()) > -1}
                    onChange={(value) => this.onChange(value)}
                    showSpinner={this.state.showSpinner}
                />

                <p>
                    Du har valgt <b>{this.state.value}</b>
                </p>
                <p>
                    <Checkbox
                        label='Vis spinner'
                        onChange={(event) => this.setState({showSpinner: !this.state.showSpinner})}
                        checked={this.state.showSpinner}
                    />
                </p>
            </span>
        );
    }
}

export default AutocompleteExample;
