import React from 'react';
import { SkjemaGruppe, Fieldset, Radio } from './../';
import generateSample from '../../../../guideline-app/app/utils/sampling/sampleDataGenerator';

const options = [
    { label: 'Bakerst', value: 'bakerst', id: 'bakerst-radio', name: 'sitteplass', key: 'bakerst' },
    { label: 'Fremst', value: 'fremst', id: 'fremst-radio', name: 'sitteplass', key: 'fremst' },
    { label: 'Midten', value: 'midten', id: 'midten-radio', name: 'sitteplass', key: 'midten' }
];

const radioChildren = (
    options.map((radioChildProps) => (<Radio {...radioChildProps} />))
);

const children = (<Fieldset legend="Hvor vil du sitte?">{radioChildren}</Fieldset>);

export default generateSample({
    baseType: SkjemaGruppe,
    modifierNames: ['feil'],
    children
});
