/* eslint-disable react/prefer-stateless-function  */
import * as PT from 'prop-types';
import * as React from 'react';
import * as classNames from 'classnames';
import SkjemaelementFeilmelding, {
    skjemaelementFeilmeldingShape,
    SkjemaelementFeilmeldingType
} from './skjemaelement-feilmelding';
import 'nav-frontend-skjema-style'; // eslint-disable-line import/extensions

const cls = (className, harFeil) => classNames(className, {
    'skjema__feilomrade--harFeil': harFeil
});

export interface SkjemaGruppeProps {
    children: React.ReactNode | React.ReactChildren | React.ReactChild;
    classname?: string;
    title?: string;
    feil?: SkjemaelementFeilmeldingType;
}

/**
 * Fieldset
 */
class SkjemaGruppe extends React.Component<SkjemaGruppeProps> {
    renderTitle() {
        return (<div className="skjemaelement__sporsmal">{this.props.title}</div>);
    }

    render() {
        const { children, className, title, feil, ...other } = this.props;
        return (
            <div className={cls(className, feil)} {...other}>
                { title && this.renderTitle() }
                {children}
                <SkjemaelementFeilmelding feil={feil} />
            </div>
        );
    }
}

(SkjemaGruppe as React.ComponentClass).propTypes = {
    /**
     * En eller flere children, oftest en eller flere .skjemaelement
     */
    children: PT.oneOfType([
        PT.arrayOf(PT.node),
        PT.node
    ]).isRequired,
    /**
     * ClassName
     */
    className: PT.string,
    /**
     * Tittel eller skjemaspørsmål for skjemagruppe
     */
    title: PT.string,
    /**
     * Hvis skjemagruppen har feil sender man inn et objekt med en feilmelding
     */
    feil: skjemaelementFeilmeldingShape
};

(SkjemaGruppe as React.ComponentClass).defaultProps = {
    className: undefined,
    feil: undefined,
    title: undefined
};

export default SkjemaGruppe;
