import * as React from 'react';
import * as PT from 'prop-types';
import * as cn from 'classnames';

import Panel from 'nav-frontend-paneler';
import NavVeileder from 'nav-frontend-veileder';
import "nav-frontend-veilederpanel-style";

const cls = (type, kompakt) => cn('veileder-panel', {
    'veileder-panel--plakat': type === 'plakat',
    'veileder-panel--kompakt': kompakt
});

export interface NavVeilederPanelProps {
    children: React.ReactNode | React.ReactChild | React.ReactChildren;
    svg: React.ReactNode | React.ReactChild | React.ReactChildren;
    sentrer?: boolean;
    farge?: string;
    type: 'normal' | 'plakat';
    kompakt?: boolean;
};

class VeilederPanel extends React.Component<NavVeilederPanelProps> {
    render() {
        const { children, svg, sentrer, farge, type, kompakt, ...other } = this.props;
        return (
            <div className={cls(type, kompakt)} {...other}>
                <NavVeileder sentrer={sentrer} farge={farge}>
                    {svg}
                </NavVeileder>
                <Panel style={{borderColor: farge}}>
                    {children}
                </Panel>
            </div>
        );
    }
}

export default VeilederPanel;
